import React, { useState, useEffect } from "react";

function CourseForm({ onAdd, onUpdate, editingCourse, cancelEdit }) {
  const [course, setCourse] = useState({ title: "", level: "", duration: "" });

  useEffect(() => {
    if (editingCourse) {
      setCourse(editingCourse);
    } else {
      setCourse({ title: "", level: "", duration: "" });
    }
  }, [editingCourse]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingCourse) {
      onUpdate(course);
    } else {
      onAdd(course);
    }
    setCourse({ title: "", level: "", duration: "" });
  };

  return (
    <form onSubmit={handleSubmit} style={{ margin: "20px auto", textAlign: "center" }}>
      <h3>{editingCourse ? "Edit Course" : "Add New Course"}</h3>
      <input
        type="text"
        placeholder="Title"
        value={course.title}
        onChange={(e) => setCourse({ ...course, title: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Level"
        value={course.level}
        onChange={(e) => setCourse({ ...course, level: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Duration"
        value={course.duration}
        onChange={(e) => setCourse({ ...course, duration: e.target.value })}
        required
      />
      <button type="submit">{editingCourse ? "Update" : "Add"}</button>
      {editingCourse && (
        <button type="button" onClick={cancelEdit}>
          Cancel
        </button>
      )}
    </form>
  );
}

export default CourseForm;
